const mensajes = items
  .slice() // copiamos el array para no mutar el original
  .reverse() // invertimos el orden
  .map(item => {
    return {
      type: item.json.message.type,
      content: item.json.message.content
    };
  });

return [
  {
    json: {
      mensajes
    }
  }
];
